import React, { useState, useEffect } from 'react';
import { View, Text, Button, TextInput, ScrollView, StyleSheet, TouchableOpacity, Alert } from 'react-native';
import { supabase } from '../../../../lib/supabase';



export default function Autres() {
    const [questions, setQuestions] = useState([]);
    const [selectedOption, setSelectedOption] = useState(null);
    const [selectedQuestionIndex, setSelectedQuestionIndex] = useState(null);
    const [answer, setAnswer] = useState('');
    const [buttonColors, setButtonColors] = useState([]);

    useEffect(() => {
        fetchQuestions();
    }, []);

    async function fetchQuestions() {
        try {
            const { data, error } = await supabase.from('questionnaire_dons_questions')
                .select(`
            idQuestion,
            libelleQuestion, 
            questionnaire_dons_details_questions(idQuestion, libelleDetailQuestion),
            sous_categorie_question(idSousCategorieQuestion, libelleSousCategorieQuestion, idCategorieQuestion)`)
                .eq('idSousCategorieQuestion', '5');
            if (error) {
                console.error('erreur ', error);
                return;
            }
            console.log(data);
            
            // getResponsesForUser(getIdQuestionnaireUserConnecte(getUserConnecte()))
            const initialButtonColors = new Array(data.length).fill({ oui: 'white', non: 'white', idk: 'white' });
            setButtonColors(initialButtonColors);
            setQuestions(data);
        } catch (error) {
            console.error(error);
        }
    }
    async function getResponsesForUser(userId) {
        try {
            const userId = await getUserConnecte();
            const idQuestionnaire = await getIdQuestionnaireUserConnecte(userId);
            const { data, error } = await supabase.rpc('get_responses_for_user', { id_user: idQuestionnaire });
    
            if (error) {
                console.error('Erreur lors de l\'appel de la fonction get_responses_for_user:', error.message);
                return null;
            }
            console.log(data);
            
            return data;
        } catch (error) {
            console.error('Erreur lors de l\'appel de la fonction get_responses_for_user:', error.message);
            return null;
        }
    }
    async function getUserConnecte() {
        const userActif = await supabase.auth.getUser();
        const userId = userActif.data.user.id;        
        return userId;
    }
   
    // async function fetchQuestions() {
    //     try {
    //         const userId = getUserConnecte();
    //         if (!userId) {
    //             console.error('Utilisateur non identifié.');
    //             return;
    //         }
    
    //         const { data: userResponses, error: responseError } = await supabase
    //             .from('liaison_questionnaire')
    //             .select('idQuestion, reponseQuestion')
    //             .eq('idQuestionnaire', userId); // Récupérer les réponses de l'utilisateur
                
    //         console.log("user responses : "+userResponses);
            
    //         if (responseError) {
    //             console.error('Erreur lors de la récupération des réponses:', responseError.message);
    //             return;
    //         }
    
    //         const { data: questionsData, error: questionsError } = await supabase
    //             .from('questionnaire_dons_questions')
    //             .select('idQuestion, libelleQuestion, questionnaire_dons_details_questions(idQuestion, libelleDetailQuestion), sous_categorie_question(idSousCategorieQuestion, libelleSousCategorieQuestion, idCategorieQuestion)')
    //             .eq('idSousCategorieQuestion', '5');
    
    //         if (questionsError) {
    //             console.error('Erreur lors de la récupération des questions:', questionsError.message);
    //             return;
    //         }
    
    //         const initialButtonColors = questionsData.map(question => {
    //             const userResponse = userResponses.find(response => response.idQuestion === question.idQuestion);
    //             if (userResponse) {
    //                 // Vérifier si la réponse de l'utilisateur à la question est "Oui"
    //                 if (userResponse.reponseQuestion === 'Oui') {
    //                     // Si la réponse est "Oui", définir la couleur du bouton "Oui" sur bleu
    //                     return { oui: 'blue', non: 'white', idk: 'white' };
    //                 } else if (userResponse.reponseQuestion === 'Non') {
    //                     // Si la réponse est "Non", définir la couleur du bouton "Non" sur bleu
    //                     return { oui: 'white', non: 'blue', idk: 'white' };
    //                 } else if (userResponse.reponseQuestion === 'Je ne sais pas') {
    //                     // Si la réponse est "je ne sais pas", définir la couleur du bouton "je ne sais pas" sur bleu
    //                     return { oui: 'white', non: 'white', idk: 'blue' };
    //                 }
    //             } else {
    //                 // Si l'utilisateur n'a pas encore répondu à cette question, définir la couleur par défaut
    //                 return { oui: 'white', non: 'white', idk: 'white' };
    //             }
    //         });
    
    //         setButtonColors(initialButtonColors);
    //         setQuestions(questionsData);
    //     } catch (error) {
    //         console.error('Erreur lors de la récupération des questions et réponses:', error.message);
    //     }
    // }

    async function getIdQuestionnaireUserConnecte(idUser) {
        try {
            const { data, error } = await supabase.rpc('get_id_questionnaire_user_connecte', { iduser: idUser });
            console.log(data);
            console.log(idUser);
            
            if (error) {
                throw new Error('Erreur lors de l\'appel de la fonction PL/pgSQL: ' + error.message);
            }

            if (data.length === 0) {
                throw new Error('ID du questionnaire non trouvé pour l\'utilisateur connecté');
            }
            
            return data;
        } catch (error) {
            console.error('Erreur:', error.message);
            return null;
        }
    }
    async function insertOrUpdateQuestionnaire(idQuestionnaire, idQuestion, reponseQuestion, idDetailQuestion, reponseDetailQuestion) {
        try {
            const { data, error } = await supabase.rpc('insert_or_update_liaison_questionnaire', {
                idquestionnaire: idQuestionnaire,
                idquestion: idQuestion,
                reponsequestion: reponseQuestion,
                iddetailquestion: idDetailQuestion,
                reponsedetailquestion: reponseDetailQuestion
            });
    
            if (error) {
                throw new Error('Erreur lors de l\'opération: ' + error.message);
            }
    
            Alert.alert('Opération réussie');
        } catch (error) {
            console.error('Erreur lors de l\'opération:', error.message);
            Alert.alert('Erreur lors de l\'opération');
        }
    }
    
    async function handleOptionSelect(option, index, idQuestion) {
        try {
            const userId = await getUserConnecte();
            const idQuestionnaire = await getIdQuestionnaireUserConnecte(userId);
    
            if (idQuestionnaire) {
                insertOrUpdateQuestionnaire(idQuestionnaire, index, option, null, answer);
            } else {
                console.error('ID du questionnaire non trouvé.');
            }
    
            setSelectedOption(option);
            setSelectedQuestionIndex(index);
            setButtonColors(prevColors => {
                const updatedColors = [...prevColors];
                const selectedQuestionColors = { oui: 'white', non: 'white', idk: 'white' };
                if (option === 'Oui') {
                    selectedQuestionColors.oui = '#0BADD5';
                } else if (option === 'Non') {
                    selectedQuestionColors.non = '#0BADD5';
                } else if (option === 'Je ne sais pas') {
                    selectedQuestionColors.idk = '#0BADD5';
                }
    
                updatedColors[index] = selectedQuestionColors;
                return updatedColors;
            });
            
            console.log(index);
            console.log(option);
            
            
        } catch (error) {
            console.error('Erreur lors de la manipulation des données:', error);
            Alert.alert('Erreur lors de la manipulation des données');
        }
    }
    

    return (
        <ScrollView>
            {questions.map((question, index) => (
                <View key={index}>
                    <Text>{question.libelleQuestion}</Text>
                    {selectedQuestionIndex === index && selectedOption === 'Oui' && question.questionnaire_dons_details_questions.map((detail, detailIndex) => (
                        <View key={detailIndex}>
                            <Text>{detail.libelleDetailQuestion}</Text>
                            <TextInput
                                placeholder="Réponse"
                                onChangeText={(text) => setAnswer(text)}
                                value={answer}
                            />
                        </View>
                    ))}
                    <View style={styles.buttonContainer}>
                        <TouchableOpacity style={[styles.buttonOui, { backgroundColor: buttonColors[index].oui }]} onPress={() => handleOptionSelect('Oui', index, question.idQuestion)}>
                            <Text style={styles.buttonText}>Oui</Text>
                        </TouchableOpacity>
                        <TouchableOpacity style={[styles.buttonIDK, { backgroundColor: buttonColors[index].idk }]} onPress={() => handleOptionSelect('Je ne sais pas', index, question.idQuestion)}>
                            <Text style={styles.buttonTextIDK}>Je ne sais pas</Text>
                        </TouchableOpacity>
                        <TouchableOpacity style={[styles.buttonNon, { backgroundColor: buttonColors[index].non }]} onPress={() => handleOptionSelect('Non', index, question.idQuestion)}>
                            <Text style={styles.buttonText}>Non</Text>
                        </TouchableOpacity>
                    </View>
                </View>
            ))}
        </ScrollView>
    );
}

const styles = StyleSheet.create({
    buttonContainer: {
        flexDirection: 'row',
        justifyContent: 'space-around',
        marginTop: 10,
    },
    buttonIDK: {
        padding: 10,
        borderRadius: 5,
        width: '40%',
        borderColor: 'black',
        borderWidth: 1
    },
    buttonOui: {
        padding: 10,
        borderRadius: 5,
        width: '25%',
        borderColor: 'black',
        borderWidth: 1
    },
    buttonNon: {
        padding: 10,
        borderRadius: 5,
        width: '25%',
        borderColor: 'black',
        borderWidth: 1
    },
    buttonText: {
        color: 'black',
        textAlign: 'center',

    },
    buttonTextIDK: {
        color: 'black',
        textAlign: 'center',

    },
});
