import { useState, useEffect } from 'react'
import { supabase } from '../lib/supabase'
import { StyleSheet, View, Alert, TouchableOpacity } from 'react-native'
import { Button, Input } from 'react-native-elements'
import { Session } from '@supabase/supabase-js'

export default function Account({ session }: { session: Session }) {
    const [loading, setLoading] = useState(true)
    const [username, setUsername] = useState('')
    const [full_name, setFullName] = useState('')
    const [gender, setGender] = useState('')


    useEffect(() => {
        if (session) getProfile()
    }, [session])

    async function getProfile() {
        try {
            setLoading(true)
            if (!session?.user) throw new Error('No user on the session!')

            const { data, error, status } = await supabase
                .from('profiles')
                .select(`username, full_name, gender`)
                .eq('id', session?.user.id)
                .single()
            if (error && status !== 406) {
                throw error
            }

            if (data) {
                setUsername(data.username)
                setFullName(data.full_name)
                setGender(data.gender)
            }
        } catch (error) {
            if (error instanceof Error) {
                console.log(error);

            }
        } finally {
            setLoading(false)
        }
    }

    async function updateProfile({
        username,
        full_name,
        gender,
    }: {
        username: string,
        full_name: string,
        gender: string
    }) {
        try {
            setLoading(true)
            if (!session?.user) throw new Error('No user on the session!')

            const updates = {
                id: session?.user.id,
                username,
                updated_at: new Date(),
                full_name,
                gender,
            }

            const { error } = await supabase.from('profiles').upsert(updates)

            if (error) {
                throw error
            }
        } catch (error) {
            console.log(error);
            if (error instanceof Error) {


            }
        } finally {
            setLoading(false)
        }
    }

    return (
        <View style={styles.container}>
            <View style={[styles.verticallySpaced, styles.mt20]}>
                <Input label="Email" value={session?.user?.email} disabled />
            </View>
            <View style={styles.verticallySpaced}>
                <Input label="Username" value={username || ''} onChangeText={(text) => setUsername(text)} />
            </View>
            <View style={styles.verticallySpaced}>
                <Input label="Full Name" value={full_name || ''} onChangeText={(text) => setFullName(text)} />
            </View>
            <TouchableOpacity
                style={[styles.button, gender === 'male' && styles.selectedMale]}
                onPress={() => setGender('male')}
            >
                <Text style={styles.buttonText}>Male</Text>
            </TouchableOpacity>
            <TouchableOpacity
                style={[styles.button, selectedGender === 'female' && styles.selectedFemale]}
                onPress={() => setGender('female')}
            >
                <Text style={styles.buttonText}>Female</Text>
            </TouchableOpacity>


            <View style={[styles.verticallySpaced, styles.mt20]}>
                <Button
                    title={loading ? 'Loading ...' : 'Update'}
                    onPress={() => updateProfile({ username, full_name, gender })}
                    disabled={loading}
                />
            </View>

            <View style={styles.verticallySpaced}>
                {/* <Button title="Sign Out" onPress={() => supabase.auth.signOut()} /> */}
                <Button title="Sign Out" onPress={() => supabase.auth.signOut()} />
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        marginTop: 40,
        padding: 12,
    },
    verticallySpaced: {
        paddingTop: 4,
        paddingBottom: 4,
        alignSelf: 'stretch',
    },
    horizontalySpaced: {
        paddingTop: 4,
        paddingBottom: 4,
        alignSelf: 'stretch',
    },
    button: {
        padding: 10,
        margin: 5,
        borderRadius: 5,
        borderWidth: 1,
        borderColor: '#000',
    },
    selectedMale: {
        backgroundColor: 'blue',
    },
    selectedFemale: {
        backgroundColor: 'pink',
    },
    buttonText: {
        color: '#fff',
        fontWeight: 'bold',
    },
    mt20: {
        marginTop: 20,
    },
})
