import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View } from 'react-native';
import IconIonicons from 'react-native-vector-icons/Ionicons'
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import Header from './composants/header';
import Accueil from './Ecrans/Accueil';
import Questionnaire from './Ecrans/Questionnaire';
import Profil from './Ecrans/Profil';
import Connexion from './Ecrans/Landing.js/Connexion';
import 'react-native-url-polyfill/auto'
import { useState, useEffect } from 'react'
import { supabase } from './lib/supabase';
import Auth from './composants/Auth';
import Account from './composants/Account';
import { Session } from '@supabase/supabase-js'

const Tab = createBottomTabNavigator();


const TabNavigator = () => (
  <Tab.Navigator screenOptions={({ route }) => ({
    tabBarShowLabel: false,
  })}>
    <Tab.Screen name="Accueil" component={Accueil}
      options={{
        headerShown: false,
        tabBarLabel: 'Accueil',
        tabBarIcon: ({ color, size }) => (
          <IconIonicons name="reader-outline" color={'black'} size={28} />
        )
      }}
    />
    <Tab.Screen name="Questionnaire" component={Questionnaire}
      options={{
        headerShown: false,
        tabBarLabel: 'Questionnaire',
        tabBarIcon: ({ color, size }) => (
          <IconIonicons name="home" color={'black'} size={28} />
          )
      }}
    />
    <Tab.Screen name="Profil" component={Profil}
      options={{
        headerShown: false,
        tabBarLabel: 'Profil',
        tabBarIcon: ({ color, size }) => (
          <IconIonicons name="person-outline" color={'black'} size={28} />
          )
      }}
    />
  </Tab.Navigator>
)


// export default function App() {
//   return (
//   //   <NavigationContainer>
//   //   <TabNavigator />
//   // </NavigationContainer>
//  <Connexion />
//   );
// }
export default function App() {
  const [session, setSession] = useState<Session | null>(null)

  useEffect(() => {
    supabase.auth.getSession().then(({ data: { session } }) => {
      setSession(session)
    })

    supabase.auth.onAuthStateChange((_event, session) => {
      setSession(session)
    })
  }, [])

  return (
    <View>
      {session && session.user ? <Account key={session.user.id} session={session} /> : <Auth />}
    </View>
  )
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
