import React from 'react';
import { View, Text, TouchableOpacity, StyleSheet } from 'react-native';
import { useNavigation } from '@react-navigation/native';


export default function InfoSante() {
    const navigation = useNavigation();

    const handleSoinRecuPress = () => {
        navigation.navigate('SoinRecu');
    };

    const handleHistoriquePress = () => {
        navigation.navigate('Historique');
    };

    const handleVaccinPress = () => {
        navigation.navigate('Vaccin');
    };

    const handleEtatSantePress = () => {
        navigation.navigate('EtatSante');
    };

    const handleAutresPress = () => {
        navigation.navigate('Autres');
    };

    return (
        <View style={styles.container}>
            <View style={styles.ligne1}>
                <TouchableOpacity style={styles.buttonLigne1} onPress={handleSoinRecuPress}>
                    <Text style={styles.categorieText}>Soin reçu</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.buttonLigne1} onPress={handleEtatSantePress}>
                    <Text style={styles.categorieText}>Etat santé</Text>
                </TouchableOpacity>
            </View>
            <View style={styles.ligne1}>
                <TouchableOpacity style={styles.buttonLigne1} onPress={handleHistoriquePress}>
                    <Text style={styles.categorieText}>Historique</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.buttonLigne1} onPress={handleVaccinPress}>
                    <Text style={styles.categorieText}>Vaccin</Text>
                </TouchableOpacity>
            </View>
            <View style={styles.ligne1}>
                <TouchableOpacity style={styles.buttonLigne2} onPress={handleAutresPress}>
                    <View style={styles.categorieContainer}>
                        <Text style={styles.categorieText}>Autres</Text>
                    </View>
                    {/* Vos autres éléments ici */}
                </TouchableOpacity>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: 'green',
        alignItems: 'center',
        justifyContent: 'center',
    },
    categorieContainer: {
        flex: 0.3,
        flexWrap: 'wrap',
        justifyContent: 'center',
        alignContent: 'center'
    },
    categorieText: {
        textAlign: 'center',
        fontSize: 20,
    },
    ligne1: {
        width: '100%',
        flexDirection: 'row',
        padding: 10,
        justifyContent: 'space-between'
    },
    ligne2: {
        width: '100%',
        padding: 10,
        height: '50%'
    },
    buttonLigne1: {
        padding: 10,
        borderRadius: 5,
        width: '48%', // Modifiez la largeur selon vos besoins
        borderColor: 'black',
        borderWidth: 1,
    },
    buttonLigne2: {
        padding: 10,
        borderRadius: 5,
        borderColor: 'black',
        borderWidth: 1,
        flex: 1,
        flexDirection: 'column',
    },
});
