import React, { useState, useEffect } from 'react';
import { View, Text, Button, TextInput, ScrollView, StyleSheet, TouchableOpacity, Alert } from 'react-native';
import { supabase } from '../../../../lib/supabase';
import AsyncStorage from '@react-native-async-storage/async-storage';



export default function Autres() {
    const [questions, setQuestions] = useState([]);
    const [selectedOption, setSelectedOption] = useState(null);
    const [selectedQuestionIndex, setSelectedQuestionIndex] = useState(null);
    const [answer, setAnswer] = useState('');
    const [buttonColors, setButtonColors] = useState([]);

    useEffect(() => {
        fetchQuestions();
        loadStoredValues(); // Chargez les valeurs stockées lors du montage du composant
    }, []);

    async function fetchQuestions() {
        try {
            const { data, error } = await supabase.from('questionnaire_dons_questions')
                .select(`
            libelleQuestion, 
            questionnaire_dons_details_questions(idQuestion, libelleDetailQuestion),
            sous_categorie_question(idSousCategorieQuestion, libelleSousCategorieQuestion, idCategorieQuestion)`)
                .eq('idSousCategorieQuestion', '5');
            if (error) {
                console.error('erreur ', error);
                return;
            }
            const initialButtonColors = new Array(data.length).fill({ oui: 'white', non: 'white', idk: 'white' });
            setButtonColors(initialButtonColors);
            setQuestions(data);
        } catch (error) {
            console.error(error);
        }
    }
    async function loadStoredValues() {
        try {
            const storedSelectedOption = await AsyncStorage.getItem('selectedOption');
            const storedSelectedQuestionIndex = await AsyncStorage.getItem('selectedQuestionIndex');
            const storedAnswer = await AsyncStorage.getItem('answer');

            if (storedSelectedOption && storedSelectedQuestionIndex && storedAnswer) {
                setSelectedOption(JSON.parse(storedSelectedOption));
                setSelectedQuestionIndex(JSON.parse(storedSelectedQuestionIndex));
                setAnswer(storedAnswer);
            }
        } catch (error) {
            console.error('Erreur lors du chargement des valeurs stockées:', error);
        }
    }

    async function storeValues(option, index) {
        try {
            await AsyncStorage.setItem('selectedOption', JSON.stringify(option));
            await AsyncStorage.setItem('selectedQuestionIndex', JSON.stringify(index));
            await AsyncStorage.setItem('answer', answer);
        } catch (error) {
            console.error('Erreur lors du stockage des valeurs:', error);
        }
    }
    async function getUserConnecte() {
        const userActif = await supabase.auth.getUser();
        const userId = userActif.data.user.id;        
        return userId;
    }
   
    async function getIdQuestionnaireUserConnecte(idUser) {
        try {
            const { data, error } = await supabase.rpc('get_id_questionnaire_user_connecte', { iduser: idUser });
            console.log(data);

            if (error) {
                throw new Error('Erreur lors de l\'appel de la fonction PL/pgSQL: ' + error.message);
            }

            if (data.length === 0) {
                throw new Error('ID du questionnaire non trouvé pour l\'utilisateur connecté');
            }
            
            return data;
        } catch (error) {
            console.error('Erreur:', error.message);
            return null;
        }
    }
    async function insertOrUpdateQuestionnaire(idQuestionnaire, idQuestion, reponseQuestion, idDetailQuestion, reponseDetailQuestion) {
        try {
            const { data, error } = await supabase.rpc('insert_or_update_liaison_questionnaire', {
                idquestionnaire: idQuestionnaire,
                idquestion: idQuestion,
                reponsequestion: reponseQuestion,
                iddetailquestion: idDetailQuestion,
                reponsedetailquestion: reponseDetailQuestion
            });
    
            if (error) {
                throw new Error('Erreur lors de l\'opération: ' + error.message);
            }
    
            Alert.alert('Opération réussie');
        } catch (error) {
            console.error('Erreur lors de l\'opération:', error.message);
            Alert.alert('Erreur lors de l\'opération');
        }
    }
    
    async function handleOptionSelect(option, index,) {
        try {
            const userId = await getUserConnecte();
            const idQuestionnaire = await getIdQuestionnaireUserConnecte(userId);
    
            if (idQuestionnaire) {
                insertOrUpdateQuestionnaire(idQuestionnaire, index, option, null, answer);
            } else {
                console.error('ID du questionnaire non trouvé.');
            }
    
            setSelectedOption(option);
            setSelectedQuestionIndex(index);
            setButtonColors(prevColors => {
                const updatedColors = [...prevColors];
                const selectedQuestionColors = { oui: 'white', non: 'white', idk: 'white' };
                if (option === 'Oui') {
                    selectedQuestionColors.oui = '#0BADD5';
                } else if (option === 'Non') {
                    selectedQuestionColors.non = '#0BADD5';
                } else if (option === 'Je ne sais pas') {
                    selectedQuestionColors.idk = '#0BADD5';
                }
    
                updatedColors[index] = selectedQuestionColors;
                return updatedColors;
            });
            
            console.log(index);
            console.log(option);
            
            await storeValues(option, index); // Stockez les valeurs sélectionnées

        } catch (error) {
            console.error('Erreur lors de la manipulation des données:', error);
            Alert.alert('Erreur lors de la manipulation des données');
        }
    }
    

    return (
        <ScrollView>
            {questions.map((question, index) => (
                <View key={index}>
                    <Text>{question.libelleQuestion}</Text>
                    {selectedQuestionIndex === index && selectedOption === 'Oui' && question.questionnaire_dons_details_questions.map((detail, detailIndex) => (
                        <View key={detailIndex}>
                            <Text>{detail.libelleDetailQuestion}</Text>
                            <TextInput
                                placeholder="Réponse"
                                onChangeText={(text) => setAnswer(text)}
                                value={answer}
                            />
                        </View>
                    ))}
                    <View style={styles.buttonContainer}>
                        <TouchableOpacity style={[styles.buttonOui, { backgroundColor: buttonColors[index].oui }]} onPress={() => handleOptionSelect('Oui', index)}>
                            <Text style={styles.buttonText}>Oui</Text>
                        </TouchableOpacity>
                        <TouchableOpacity style={[styles.buttonIDK, { backgroundColor: buttonColors[index].idk }]} onPress={() => handleOptionSelect('Je ne sais pas', index)}>
                            <Text style={styles.buttonTextIDK}>Je ne sais pas</Text>
                        </TouchableOpacity>
                        <TouchableOpacity style={[styles.buttonNon, { backgroundColor: buttonColors[index].non }]} onPress={() => handleOptionSelect('Non', index)}>
                            <Text style={styles.buttonText}>Non</Text>
                        </TouchableOpacity>
                    </View>
                </View>
            ))}
        </ScrollView>
    );
}

const styles = StyleSheet.create({
    buttonContainer: {
        flexDirection: 'row',
        justifyContent: 'space-around',
        marginTop: 10,
    },
    buttonIDK: {
        padding: 10,
        borderRadius: 5,
        width: '40%',
        borderColor: 'black',
        borderWidth: 1
    },
    buttonOui: {
        padding: 10,
        borderRadius: 5,
        width: '25%',
        borderColor: 'black',
        borderWidth: 1
    },
    buttonNon: {
        padding: 10,
        borderRadius: 5,
        width: '25%',
        borderColor: 'black',
        borderWidth: 1
    },
    buttonText: {
        color: 'black',
        textAlign: 'center',

    },
    buttonTextIDK: {
        color: 'black',
        textAlign: 'center',

    },
});
