import React, { useState, useEffect } from 'react';
import { View, Text, Button, TextInput, ScrollView } from 'react-native';
import { supabase } from '../lib/supabase';

export default function Info() {
    const [questions, setQuestions] = useState([]);
    const [selectedQuestionDetail, setSelectedQuestionDetail] = useState(null);
    const [selectedOption, setSelectedOption] = useState(false);
    const [answer, setAnswer] = useState('');

    useEffect(() => {
        fetchQuestions();
    }, []);

    async function fetchQuestions() {
        try {
            const { data, error } = await supabase.from('questionnaire_dons_questions').select(`
            libelleQuestion, 
            questionnaire_dons_details_questions(idQuestion, libelleDetailQuestion)
          `);

            if (error) {
                console.error('erreur ', error);
                console.log('question ', data);
                return;
            }
           

            setQuestions(data);
        } catch (error) {
            console.error(error);
        }
    }

    function handleOptionSelect(option) {
        setSelectedOption(option);
        console.log(option);
        
        if (option === 'Oui') {
            // Si l'option est "Oui", afficher la question détaillée par défaut
            setSelectedQuestionDetail(true);
        } else {
            // Sinon, désélectionner la question détaillée
            setSelectedQuestionDetail(false);
        }
    }

    return (
        <ScrollView>
            {questions.map((question, index) => (
                <View key={index}>
                    <Text>{question.libelleQuestion}</Text>
                    {question.questionnaire_dons_details_questions.map((detail, detailIndex) => (
                        <View key={detailIndex}>
                            <Text>{detail.libelleDetailQuestion}</Text>
                            {detail.libelleDetailQuestion === selectedQuestionDetail && selectedOption === 'Oui' && (
                                <TextInput
                                    placeholder="Réponse"
                                    onChangeText={(text) => setAnswer(text)}
                                    value={answer}
                                />
                            )}
                        </View>
                    ))}
                    <View style={{ flexDirection: 'row', justifyContent: 'space-around', marginTop: 10 }}>
                        <Button title="Oui" onPress={() => handleOptionSelect('Oui')} />
                        <Button title="Non" onPress={() => handleOptionSelect('Non')} />
                        <Button title="Je ne sais pas" onPress={() => handleOptionSelect('Je ne sais pas')} />
                    </View>
                </View>
            ))}
        </ScrollView>
    );
}
