import React, { useState, useEffect } from 'react';
import { View, Text, Button } from 'react-native';
import { supabase } from '../lib/supabase';

export default function Info() {
    const [questions, setQuestions] = useState([]);

    useEffect(() => {
        fetchQuestions();
    }, []);

    async function fetchQuestions() {
        try {
            const sql = `
            SELECT qd."idQuestion", qd."libelleQuestion", dqd."idQuestion" AS "usedInDetails", dqd."libelleDetailQuestion" AS "detailsLibelleQuestion"
            FROM questionnaire_dons_questions qd
            LEFT OUTER JOIN questionnaire_dons_details_questions dqd ON qd."idQuestion" = dqd."idQuestion"
            ORDER BY qd."idQuestion";
        `;

            const { data, error } = await supabase.rpc('execute', { sql });


            if (error) {
                console.error('erreur ', error);
                return;
            }
            console.log('question ', data);

            setQuestions(data);
        } catch (error) {
            console.error(error);
        }
    }

    return (
        <View>
            {questions.map((question, index) => (
                <View key={index}>
                    <Text>{question.libelleQuestion}</Text>
                    <Button title="Oui" onPress={() => handleOptionSelect('Oui')} />
                    <Button title="Non" onPress={() => handleOptionSelect('Non')} />
                    <Button title="Je ne sais pas" onPress={() => handleOptionSelect('Je ne sais pas')} />
                </View>
            ))}
        </View>
    );

    function handleOptionSelect(option) {
        // Ajoutez ici le code pour gérer la sélection de l'option
        console.log('Option sélectionnée:', option);
    }
}
